import React, { useState } from "react";
import {
  Button,
  StyleSheet,
  Text,
  View,
  Dimensions,
  Modal,
  TouchableHighlight,
  Image,
  TextInput,
  Alert,
  TouchableOpacity,
  Pressable,
} from "react-native";
import { Calendar } from "react-native-calendars";
import { Provider } from "react-native-paper";
import { firebase } from "../firebase";
import { getCurrentDate } from "../HelperFunctions";
import BottomSheet from "./BottomSheet";
import DateTimePickerModal from "react-native-modal-datetime-picker";
import { Card, Avatar } from "react-native-paper";
import { useNavigation } from "@react-navigation/core";
import { AntDesign } from "@expo/vector-icons";
import "../global";
import { createTopBar } from "../HelperFunctions";
import {schedulePushNotification} from "./SendNotification"



const { width, height } = Dimensions.get("screen");
const SPACING = 20;
const ICON_SIZE = 75;
const initialDate = getCurrentDate();
const today = new Date();
const compare = (obj1, obj2) => {
  return JSON.stringify(obj1) === JSON.stringify(obj2);
};

const generateColor = () => {
  const randomColor = Math.floor(Math.random() * 16777215)
    .toString(16)
    .padStart(6, "0");
  return `#${randomColor}`;
};

const randomColor = generateColor();
const randomColor2 = generateColor();
async function eventsData(onReceiveList) {
  const events = [];
  var snapshot = await firebase
    .firestore()
    .collection("events")
    .get()
    .then((querySnapshot) => {
      querySnapshot.forEach(function (doc) {
        let start = doc.data().start_date,
          end = doc.data().end_date,
          name = doc.data().name,
          id = doc.data().id,
          programme = doc.data().programme,
          key = events.length + 1;
        let date = new Date();
        let date2 = new Date(start);
        let diff = Math.ceil((date2 - date) / (1000 * 3600 * 24));
        if (Math.abs(diff) < 90) {
          events.push({
            name: name,
            key: key,
            start: start,
            end: end,
            id: id,
            programme: programme,
          });
        }
      });
    });

  onReceiveList(events);
}




const validateInput = (eventtext, notetext) => {
  let valid = true;
  if (JSON.stringify(eventtext).length - 2 <= 0) {
    Alert.alert("Warning", "Event name cannot be left empty", [
      { text: "OK", onPress: () => {} },
    ]);
    valid = false;
  }

  if (JSON.stringify(notetext).length - 2 <= 0) {
    Alert.alert("Warning", "Event programme cannot be left empty", [
      { text: "OK", onPress: () => {} },
    ]);
    valid = false;
  }
  return valid;
};

async function sendToFirestore(
  eventtext,
  notetext,
  startdatetext,
  enddatetext
) {
  if (validateInput(eventtext, notetext) == true) {
    await firebase
      .firestore()
      .collection("events")
      .add({
        name: eventtext,
        start_date: startdatetext,
        end_date: enddatetext,
        id: Math.max(50, Math.floor(Math.random() * 150)),
        programme: notetext,
      })
      .then(() => {
        Alert.alert("Event Added");
      });
  }
}

const EventsCalendar = () => {
  const [EventsState, SetEventsState] = useState({
    events: [
      {
        key: 0,
        programme: "",
        name: "",
        id: 0,
        start: "",
        end: "",
      },
    ],
  });

  const onReceive = (newEvents) => {
    // when the list is received we set our protocolLIst to the current received list (updating)
    SetEventsState((prevState) => ({
      events: (prevState.events = newEvents),
    }));
  };
  eventsData(onReceive);

  const [dayData, setDayData] = useState([{}]);
  const navigation = useNavigation();

  const GetMarkedDates = () => {
    let markedDay = {};

    EventsState.events.map((item, key) => {
      if (compare(item.start, item.end)) {
        markedDay[item.start] = {
          startingDay: true,
          endingDay: true,
          selected:true,
          marked: true,
          color: randomColor2,
          dotColor: "blue",
        };
      } else {
        markedDay[item.start] = {
          startingDay: true,
          color: randomColor,
          textColor: "white",
          marked: true,
          dotColor: "black",
        };
        markedDay[item.end] = {
          endingDay: true,
          color: randomColor,
          textColor: "white",
          marked: true,
          dotColor: "black",
        };
      }
    });

    return markedDay;
  };

  const getDayEvents = (day) => {
    const data = [];
    EventsState.events.map((event, key) => {
      if (compare(event.start, day.dateString) == true) {
        data.push(event);
      }
    });
    setDayData(data);
  };


  const [eventName, setEventName] = useState("");
  const [eventProgramme, setEventProgramme] = useState("");
  const [startDate, setStartDate] = useState(
    new Date().toISOString().split("T")[0]
  );
  const [endDate, setEndDate] = useState(
    new Date().toISOString().split("T")[0]
  );
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedEnd, setSelectedEnd] = useState(new Date());
/*   const [btnstart, setBtnStart] = useState(false);
  const [btnend, setBtnEnd] = useState(false); */
  const [modalVisible, setModalVisible] = useState(false);
  const [datePickerVisible, setDatePicker] = useState(false);
  const [EnddatePickerVisible, setDatePickerEnd] = useState(false);

  return (
    <View style={styles.container}>
      {createTopBar(10, navigation)}
      <Modal
        style={styles.modalStyle}
        animationType={"fade"}
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          Alert.alert("Modal has been closed.");
          setModalVisible(false);
        }}
      >
        <View style={styles.modalStyle}>
          <Text style={{ marginTop:50, fontSize: 25, top: 10, textAlign: "center" }}>
            Create Event
          </Text>
          <TextInput
            style={styles.input}
            underlineColorAndroid="transparent"
            placeholder="Title"
            placeholderTextColor="black"
            autoCapitalize="none"
            onChangeText={(newText) => {
              setEventName(newText);
            }}
          />
          <TextInput
            style={styles.input}
            underlineColorAndroid="transparent"
            placeholder="Program"
            placeholderTextColor="black"
            autoCapitalize="none"
            onChangeText={(newText) => {
              setEventProgramme(newText);
            }}
          />

          <Pressable
            style={({ pressed }) => [
              {
                backgroundColor: pressed ? "rgb(210, 230, 255)" : "#2c477a",
              },
              {
                borderRadius: 10,
                margin: 10,
                flexDirection: "row",
                marginTop: 40,
              },
            ]}
            onPress={() => {
              setDatePicker(true);
            }}
          >
            <Text style={{ margin: 30 }}>Start</Text>
