import React, { useState } from "react";
import { useNavigation } from "@react-navigation/core";
import {
  StatusBar,
  FlatList,
  Image,
  Animated,
  Text,
  View,
  Dimensions,
  StyleSheet,
  TouchableOpacity,
  TouchableHighlight,
  Easing,
  SafeAreaViewBase,
  SafeAreaView,
} from "react-native";
const { width, height } = Dimensions.get("screen");
import { LinearGradient } from "expo-linear-gradient";
import "../global";

//Constants for use with UI scaling
const buttonHeight = 50;
const textPos = buttonHeight / 2;
const SPACING = 20;
const AVATAR_SIZE = 70;
const ICON_SIZE = 80;
const image = { uri: "https://reactjs.org/logo-og.png" };
const witsLogo = {
  uri: "https://www.wits.ac.za/media/wits-university/news-and-events/images/logos-and-icons/Wits-Logo-Mono-Neg-Legal-600x300.png",
};



//data to be displayed in the flatlist
const DATA = [


  {
    id: "58694a0f-3da1-471f-bd96-145571e29d72",
    title: "Calendar",
    destination: "CalendarView",
    image: require("./calendar.png"),
  },
  {
    id: "38694a0f-3da1-471f-bd96-145571e29d72",
    title: "Assignments",
    destination: "BackgroundTest",
    image: require("./assignment.png"),
  },
  {
    id: "28694a0f-3da1-471f-bd96-145571e29d72",
    title: "Tasks",
    destination: "BackgroundTest",
    image: require("./tasks.png"),
  },
  {
    id: "18694a0f-3da1-471f-bd96-145571e29d72",
    title: "Logbook",
    destination: "BackgroundTest",
    image: require("./logbook.png"),
  },

  {
    id: "08694a0f-3da1-471f-bd96-145571e29d72",
    title: "Settings",
    destination: "SettingsView",
    image: require("./settings.png"),
  },

  {
    id: "3ac68afc-c605-48d3-a4f8-fbd91aa97f63",
    title: "Logout",
    destination: "Login",
    image: require("./logout.png"),
  },
];



// creates it so each item has a touchable button with correct title
const Item = ({ item, onPress, backgroundColor, textColor }) => (
  <TouchableOpacity onPress={onPress} style={[styles.item, backgroundColor]}>
    <Text style={[styles.title, textColor]}>{item.title}</Text>
  </TouchableOpacity>
);

const Dashboard = () => {

  //use navigation
  const navigation = useNavigation();


  //change colour of tapped button
  const [selectedId, setSelectedId] = useState(null);
  const [name, SetName] = useState("");

  // first render function that renders the vertical flatlist
  const renderItem = ({ item }) => {
    const backgroundColor = item.id === selectedId ? "#7fb4e1" : "#bcbcbc";
    const color = item.id === selectedId ? "black" : "black";
    return (
      <View
        style={{
          flexDirection: "row",
          padding: SPACING,
          backgroundColor: " rgba(255,255,255,0.1)",
          flex: 1,
          borderRadius: 8,
          marginBottom: SPACING,
          shadowColor: "rgba(0,0,0,1)",
          shadowOffset: {
            width: 0,
            height: 10,
          },
          paddingTop: 10,
          shadowOpacity: 1,
          shadowRadius: 20,
        }}
      >

        
        <View>
          
          <Image
          //create an image with the appropriate locally stored icon
            source={item.image}
            style={{
              position:'absolute',
              width: AVATAR_SIZE,
              height: AVATAR_SIZE,
              borderRadius: AVATAR_SIZE,
              marginRight: SPACING / 2,
              zIndex:1
            }}
          ></Image>
        </View>
        <View style={{ width: "75%", flex: 1 }}>
          <LinearGradient
          //add a button to each item to allow navigation
            colors={[
              "rgba(30,55,108,1)",
              " rgba(30,55,108,0.8)",
              "rgba(30,55,108,1)",
            ]}
            style={styles.linearGradient}
          >
            <TouchableOpacity
              onPress={() => {
                navigation.navigate(item.destination);
              }} 
            >
              <Text style={styles.buttonText}>{item.title}</Text>
            </TouchableOpacity>
          </LinearGradient>
        </View>
      </View>
    );
  };



    // second render function that renders the horizontal flatlist
  const renderItem2 = ({ item }) => {
    return (
      <View
        style={{
          flexDirection: "row",
          padding: SPACING,
          flex: 1,
          paddingTop: 10,
          borderRadius: 100,
          marginBottom: SPACING,
          paddingBottom:SPACING,
          paddingBottom: 5,
        }}
      >
        <View>
          <TouchableHighlight
            onPress={() => {
              navigation.navigate(item.destination);
            }}
            underlayColor="rgba(0,0,0,0)"
          >
            <Image
            //create an image with the appropriate locally stored icon that is tappable
              source={item.image}
              style={{
                width: AVATAR_SIZE / 1.5,
                height: AVATAR_SIZE / 1.5,
                borderRadius: AVATAR_SIZE / 1.5,
                
              }}
            ></Image>
          </TouchableHighlight>
        </View>
      </View>
    );
  };

  return (

    // main render function that dictates styling
    <SafeAreaView style={{ flex: 1, backgroundColor: " rgba(255,255,255,1)" }}>
      <View
        style={{
          flex: 1,
          backgroundColor: " rgba(30,55,108,1)",
          borderRadius: 15,
        }}
      >
        <View
          style={{
            height: ICON_SIZE + 18,
            backgroundColor: "  rgba(30,55,108,1)",
            borderRadius: 10,
          }}
        >
          <View>
            <Image
              style={{
                width: 240,
                height: 120,
                position: "absolute",
                left: SPACING,
                top: -10,
                resizeMode: "stretch",
                marginRight: SPACING / 2,
              }}
              source={witsLogo}
            />

            <TouchableHighlight
            underlayColor="rgba(0,0,0,0)"
              onPress={() => {
                navigation.navigate("SettingsView");
              }}
            >
              <Image
                style={{
                  width: ICON_SIZE,
                  height: ICON_SIZE,
                  marginLeft:width-ICON_SIZE,
                  resizeMode: "cover",
                  right: 0,
                  top: 10,
                  borderRadius: ICON_SIZE,
                  marginRight: SPACING / 2,
                  borderWidth: 0,
                  borderColor: "rgba(0,0,0,1)",
                  backgroundColor: "rgba(255,255,255,1)",
                  shadowOffset: {
                    height: 10,
                    shadowColor: "black",
                  },
                  shadowOpacity: 1,
                  shadowRadius: 20,
                }}
                source={{ uri: authUserProfilePic }}
              />
            </TouchableHighlight>
          </View>
        </View>
        <View style={{ flex: 1, backgroundColor: " rgba(18,33,65,0.8)", paddingBottom:SPACING, }}>
          <FlatList
            contentContainerStyle={{
              padding: SPACING ,
              paddingTop: SPACING,
              paddingBottom:SPACING,
              
            }}
            data={DATA}
            renderItem={renderItem2}
            keyExtractor={(item) => item.id}
            extraData={selectedId}
            horizontal={true}
          />
        </View>

        <View style={{ flex: 6.5, backgroundColor: " rgba(18,33,65,0.8)" }}>
          <FlatList
            contentContainerStyle={{
              padding: SPACING / 8,
              paddingTop: SPACING,
            }}
            data={DATA}
            renderItem={renderItem}
            keyExtractor={(item) => item.id}
            extraData={selectedId}
          />
        </View>
      </View>
      <View style={{backgroundColor:'rgba(18,33,65,1)'}}>
        <TouchableHighlight
        underlayColor="rgba(0,0,0,0)"
          onPress={() => {
            navigation.navigate("Protocols");
          }}
        >
          <Image
            style={{
              width: ICON_SIZE,
              height: ICON_SIZE,
              marginLeft:width-ICON_SIZE,
              right: 0,
              bottom: 0,
              backgroundColor:'rgba(18,33,65,1)',
              borderRadius: ICON_SIZE,
              marginRight: SPACING / 2,
            }}
            source={require("./emergency.gif")}
          />
        </TouchableHighlight>
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    backgroundColor: "#7fb4e1",
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 16,
  },
  title: {
    fontSize: 32,
  },
  ButtonStyle: {
    height: AVATAR_SIZE,
  },
  linearGradient: {
    height: AVATAR_SIZE,
    paddingLeft: 15,
    paddingRight: 15,
    borderRadius: 5,
    paddingTop: AVATAR_SIZE / 4,
  },
  buttonText: {
    fontSize: AVATAR_SIZE / 3,
    fontWeight: "700",
    textAlign: "center",
    color: " rgba(128,163,198,0.8)",
    backgroundColor: "transparent",
  },
  stretch: {
    width: 400,
    height: 200,
    resizeMode: "stretch",
  },
  BackGround: {
    width: "100%",
    height: "100%",
    resizeMode: "stretch",
    position: "absolute",
    top: 0,
    left: 0,
    zIndex: 0,
  },
  input: {
    backgroundColor: "white",
    padding: 10,
    borderRadius: 15,
    marginTop: 10,
  },
  image: {
    flex: 1,
    justifyContent: "center",
  },
});

export default Dashboard;
