    width:"50%",
    backgroundColor:"white",
    marginTop:50,
    borderColor:"lightblue",
    borderWidth:2,
    borderRadius:10,
    alignItems:'center'
},
SPSStyle:{
    backgroundColor:"grey",
    marginTop:50,
    borderColor:"black",
    borderWidth:2,
    borderRadius:10,
    alignItems:'center'
},

buttonText:{
color:"blue",
