import React, { useState } from "react";
import { useNavigation } from "@react-navigation/core";
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  Button,
  Linking,
  TouchableHighlight,
  TouchableOpacity
} from "react-native";
import {LinearGradient} from 'expo-linear-gradient';
import { getSchedule } from './RetrieveSchedules';
import { SafeAreaView } from "react-native-safe-area-context";
import { ScrollView } from "react-native-web";

const buttonHeight=50;
const textPos=buttonHeight/2;
const SPACING = 20;
const AVATAR_SIZE = 70;
const ICON_SIZE = 260;

const Schedule = () => {
  const[state,setState]=useState({scheduleList: [{key:0,
  created_at:"",
  hospital:"",
  ID:"",
  specialty_duration:"",
  specialty_id:"",
  student_id:"",
  updated_at:"",
  week_no:"",
  }]}) 


  const onReceive = (scheduleList) => {

    setState(prevState => ({
      scheduleList: prevState.scheduleList = scheduleList,

    }));
  }
  getSchedule(onReceive)
  console.log(state.scheduleList)
  const navigation = useNavigation();
  const [name, SetName] = useState("");

  return (

    <SafeAreaView style={{flex:1}}>
      <View style ={styles.container}>

        <ScrollView style ={styles.container} >
            {
              state.protocolList.map((item,key)=>(

                <View>
                <TouchableOpacity style = {styles.item} onPress={()=>toogle(key)} >

                  <Text style={styles.itemText}>
                    {item.Protocol}
                  </Text>
                  <Text style={styles.itemText}>
                    {selected===key?"-":"+"}
                  </Text>

                </TouchableOpacity>
                <View style={selected===key? styles.content_show:styles.contentHide}>
                    <Text>{item.content}</Text>
                </View>
              </View>

              )

              )
            }
        </ScrollView>
      </View>


    </SafeAreaView>
     );
};

const styles = StyleSheet.create({
  linearGradient: {
    height: AVATAR_SIZE,
    paddingLeft: 15,
    paddingRight: 15,
    borderRadius: 5,
    
  },
  buttonText: {
    fontSize: AVATAR_SIZE/3,
    fontWeight:'700',
    textAlign: 'center',
    color: ' rgba(128,163,198,0.8)',
    backgroundColor: 'transparent',
    height:AVATAR_SIZE,
    paddingTop:AVATAR_SIZE/4
  },
});

export default Schedule;
