  View,
  Text,
  TextInput,
  Button,
  Image,
  Linking,
  TouchableOpacity,

} from "react-native";
import { Alert } from "react-native";
import {LinearGradient} from 'expo-linear-gradient';

const buttonHeight=50;
const textPos=buttonHeight/2;
const SPACING = 20;
const AVATAR_SIZE = 70;
const ICON_SIZE = 80;

const Settings = () => {
  const navigation = useNavigation();
  const [name, SetName] = useState("");

  return (
    <>
    
    <Image 
       style={{width:ICON_SIZE,
        height:ICON_SIZE,
        position: 'absolute',
        resizeMode: 'cover',
                right: 0,
                top: 10,
        borderRadius:ICON_SIZE
        ,marginRight:SPACING/2,
        borderWidth:0,
        borderColor:"rgba(0,0,0,1)",
        backgroundColor:"rgba(255,255,255,1)",
      shadowOffset:{
      height:10,
      shadowColor:'black'
      },
       shadowOpacity:1,
      shadowRadius:20,
        }}
        source={require("./bear.gif")}
        />
      <View style={styles.button}>
        <Button
          title="Change Profile Photo"
          onPress={() => {
            Alert.alert("TODO:", "Update User' profile_photo url in Firebase.");
          }}
        ></Button>
      </View>
      <LinearGradient colors={['rgba(30,55,108,1)', ' rgba(30,55,108,1)', 'rgba(30,55,108,1)']} style={styles.linearGradient}>
      <TouchableOpacity
            onPress={()=>{navigation.navigate(item.destination)}}   // when user clicks on login button 
            >
            <Text style={styles.buttonText}>Change</Text>

        </TouchableOpacity> 
    </LinearGradient>
      <View style={styles.button}>
        <Button
          title="Change App Theme"
          onPress={() => {
            Alert.alert(
              "TODO:",
              "Display app theme options as dropdown. \nPlace current theme on the top."
            );
          }}
        ></Button>
      </View>
      <View style={styles.button}>
        <Button
          title="Suggestions?"
          onPress={() => {
            Alert.alert(
              "TODO:",
              "Add suggestions, placeholder. \nAdd suggestions on Discord."
            );
          }}
        ></Button>
      </View>
    </>
  );
};

const styles = StyleSheet.create({
  button: {
    alignItems: "center",
    justifyContent: "center",
    paddingVertical: 12,
    paddingHorizontal: 32,
    borderRadius: 4,
    elevation: 3,
    marginTop:1,
    marginBottom:1,
    backgroundColor: "rgba(18,33,65,0.8)",
  },
  text: {
    fontSize: 16,
    lineHeight: 21,
    fontWeight: "bold",
    letterSpacing: 0.25,
    color: "white",
  },
  linearGradient: {
    height: AVATAR_SIZE,
    paddingLeft: 15,
    paddingRight: 15,
    borderRadius: 5,
    paddingTop: AVATAR_SIZE/4,
  },
  buttonText: {
    fontSize: AVATAR_SIZE/3,
    fontWeight:'700',
    textAlign: 'center',
    color: ' rgba(128,163,198,0.8)',
    backgroundColor: 'transparent',
  },
});

export default Settings;
